import React,{useState} from 'react';
import './App.css';
import Calender from './Components/Calender';
import {Routes, Route } from "react-router-dom"
import axios from 'axios';

function App() {
  const [data,setData] = useState([]);
  const handleAddAppointment = async (appointment) => {
    console.log("from app",appointment);

    const response = await fetch('/Add-appointment', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(appointment),
    });
    setData(response);
    console.log(data);
  }

  const handleDeleteAppointment = async (appointmentId) => {
    await axios.post(`/api/appointments/${appointmentId}`, {
      method: "DELETE",
    });
  };



  return (
    <div className="App">

      <Routes>
     {/* <Route path="/" element={<Calender />} exact/> */}
     <Route path="/Add-appointment" element={<Calender onAdd={handleAddAppointment}  onDelete={handleDeleteAppointment}/>} />
     {/* <Calender/> */}
     </Routes>

    </div>
  );
}

export default App;
