import React, { useState, useEffect } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";

import withDragAndDrop from 'react-big-calendar/lib/addons/dragAndDrop';
import 'react-big-calendar/lib/addons/dragAndDrop/styles.css';
// import events from "./events";







const localizer = momentLocalizer(moment);

const Calender = ({ onAdd, onDelete }) => {
    const DragAndDropCalendar = withDragAndDrop(Calendar)
  const [appointments, setAppointments] = useState([]);
  console.log("appointments",appointments);
  // Fetch appointments from the database
  useEffect(() => {
    async function fetchAppointments() {
      const response = await fetch("/api/appointments");
      const data = await response.json();
      setAppointments(data);
    }
    fetchAppointments();
  }, []);

  // Handle adding appointments
  const handleSelectSlot = (slotInfo) => {
    const title = prompt("Enter appointment title:");
    if (title) {
      const newAppointment = {
        id: Date.now(),
        title,
        start: slotInfo.start,
        end: slotInfo.end,
      };
      setAppointments([...appointments, newAppointment]);
      onAdd(newAppointment); // Update in the database
    }
  };

  // Handle deleting appointments
  const handleEventDelete = (appointmentId) => {
    setAppointments(appointments.filter((appt) => appt.id !== appointmentId));
    onDelete(appointmentId); // Delete from the database
  };


  const handleEventDrop = ({ event, start, end }) => {
    console.log("dddd",event,start,end);
    const updatedEvent = { ...event, start, end };
    setAppointments((prev) =>
      prev.map((appt) => (appt.id === event.id ? updatedEvent : appt))
    );
    onAdd(updatedEvent); // Update in the database
  };


  return (
    <div>
      <Calendar
        localizer={localizer}
        events={appointments}
        defaultView="week"
        selectable
        DragAndDropCalendar={DragAndDropCalendar}
        onSelectSlot={handleSelectSlot}
        onSelectEvent={(event) =>
          window.confirm("Delete this appointment?") &&
          handleEventDelete(event.id)
        }
        resizable
        onEventDrop={handleEventDrop}
        onEventResize={handleEventDrop}
        style={{ height: "80vh" }}
      />

<DragAndDropCalendar
    localizer={localizer}
    events={appointments}
    defaultView="week"
    selectable
    
    resizable
    onEventDrop={handleEventDrop}
    onEventResize={handleEventDrop}
    style={{ height: "80vh" }}
  />

    </div>
  );
};

export default Calender;
