import React,{useState} from 'react';
import './App.css';
import Calender from './Components/Calender';
import {Routes, Route } from "react-router-dom"
import axios from 'axios';
import {Toaster} from 'react-hot-toast';
import {toast} from 'react-hot-toast';

function App() {
  const [data,setData] = useState([]);
  const handleAddAppointment = async (appointment) => {
    console.log("from app",appointment);

    const response = await axios.post('/Add-appointment', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(appointment),
    });
    setData(response.data);
    console.log("Res",data);
    if(response.data.status === 200){
      toast.success(response.data.message);
    }

  }

  const handleDeleteAppointment = async (appointmentId) => {
    console.log("from delete",appointmentId);
   const res =  await axios.post(`/Delete-appointment/${appointmentId}`, {
      method: "POST",
    });
    console.log(res);
  };


  const handleUpdateAppointment = async (appointmentId) => {
    console.log("from update",appointmentId);

    const response = await axios.post('/Update-appointment', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(appointmentId),
    });

    // console.log("sdssdsdsdsdsd",data);
    // if(response.data.status === 200){
    //   toast.success(response.data.message);
    // }

  }


  return (
    <div className="App">
     <Toaster position='top-right' toastOptions={{duration: 5000}}/>
      <Routes>
     {/* <Route path="/" element={<Calender />} exact/> */}
     <Route path="/Add-appointment" element={<Calender onAdd={handleAddAppointment}  onDelete={handleDeleteAppointment}   onUpdate={handleUpdateAppointment}/>} exact/>
     {/* <Calender/> */}
     </Routes>

    </div>
  );
}

export default App;
