import './App.css';
import Calender from './Components/Calender';
import { BrowserRouter as Router, Routes,Route, Link } from "react-router-dom";
import axios from 'axios';

function App() {

  const handleAddAppointment = async (appointment) => {
    console.log("from app",appointment);
    await axios.post("/test", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(appointment),
    });
  };

  const handleDeleteAppointment = async (appointmentId) => {
    await axios.post(`/api/appointments/${appointmentId}`, {
      method: "DELETE",
    });
  };



  return (
    <div className="App">
     <Router>
      <Routes>
     <Route path="/" element={<Calender onAdd={handleAddAppointment}     onDelete={handleDeleteAppointment}/>} exact/>
     <Calender/>
     </Routes>
     </Router>
    </div>
  );
}

export default App;
