import React, { useState, useEffect } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import {toast} from 'react-hot-toast';
import axios from "axios";
import "react-big-calendar/lib/css/react-big-calendar.css";


import withDragAndDrop from 'react-big-calendar/lib/addons/dragAndDrop';
import 'react-big-calendar/lib/addons/dragAndDrop/styles.css';








const localizer = momentLocalizer(moment);

const Calender = ({ onAdd, onDelete,onUpdate }) => {
    const DragAndDropCalendar = withDragAndDrop(Calendar)
  const [appointments, setAppointments] = useState([]);
  const [data,setData] = useState([]);
  console.log(appointments);

  // Fetch appointments from the database
  useEffect(() => {
    async function fetchAppointments() {
      const response = await fetch("/");
      const data = await response.json();
      console.log(data);

      const appointments =  await Calender.find({});
      if(appointments){
        res.send({status:200, message:" Appointments !" , success:true,data:appointments});
      }
      setAppointments(data);
    }
    fetchAppointments();
  }, []);

  // Handle adding appointments
  const handleSelectSlot = async(slotInfo) => {
    const title = prompt("Enter appointment title:");
    if (title) {
      const newAppointment = {
        id: Date.now(),
        title,
        start: slotInfo.start,
        end: slotInfo.end,
      };
      setAppointments([...appointments, newAppointment]);
      // onAdd(handleAddAppointment); // Update in the database
      
    const response = await axios.post('/Add-appointment', newAppointment);
    setData(response.data);
    if(response.data.status === 200){
      toast.success(response.data.message);
    }



    }
  };

  // Handle deleting appointments
  const handleEventDelete = async(appointmentId) => {
    setAppointments(appointments.filter((appt) => appt.id !== appointmentId));
    // onDelete(appointmentId); // Delete from the database
    const response = await axios.post('/Delete-appointment',appointmentId);
    if(response.data.status === 200){
      toast.success(response.data.message);
    }
  };


  const handleEventDrop = async({ event, start, end }) => {
   
    const updatedEvent = { ...event, start, end };

    setAppointments((prev) =>
      prev.map((appt) => (appt.id === event.id ? updatedEvent : appt))
    );
    // onUpdate(handleUpdateAppointment()); // Update in the database

    const response = await axios.post('/Update-appointment',updatedEvent);
    if(response.data.status === 200){
      toast.success(response.data.message);
    }


  };

  // const handleAddAppointment = async (appointment) => {
  //   console.log("from app",appointment);

  //   const response = await axios.post('/Add-appointment', appointment);
  //   setData(response.data);
  //   console.log("Res",data);
  //   if(response.data.status === 200){
  //     toast.success(response.data.message);
  //   }

  // }



  // const handleDeleteAppointment = async (appointmentId) => {
  //   console.log("from delete",appointmentId);
  //   const response = await axios.post('/Delete-appointment',appointmentId);
  //   if(response.data.status === 200){
  //     toast.success(response.data.message);
  //   }
  // };

  // const handleUpdateAppointment = async (appointmentId) => {
  //   console.log("from update",appointmentId);

  //   const response = await axios.post('/Update-appointment',appointmentId);
  //   console.log("Updated",response.data);
  //   if(response.data.status === 200){
  //     toast.success(response.data.message);
  //   }
  // }


  return (
    <div>
      {/* <Calendar
        localizer={localizer}
        events={appointments}
        defaultView="week"
        selectable
        DragAndDropCalendar={DragAndDropCalendar}
        onSelectSlot={handleSelectSlot}
        onSelectEvent={(event) =>
          window.confirm("Delete this appointment?") &&
          handleEventDelete(event.id)
        }
        resizable
        onEventDrop={handleEventDrop}
        onEventResize={handleEventDrop}
        style={{ height: "80vh" }}
      /> */}

<DragAndDropCalendar
    localizer={localizer}
    events={appointments}
    defaultView="week"
    selectable
    onSelectSlot={handleSelectSlot}
    onSelectEvent={(event) =>
      window.confirm("Delete this appointment?") &&
      handleEventDelete(event.id)
    }
    resizable
    onEventDrop={handleEventDrop}
    onEventResize={handleEventDrop}
  
    style={{ height: "80vh" }}
  />

    </div>
  );
};

export default Calender;
